@use "abstract" as *;
@use "base/colors" as c;

.stat {
  background-color: c.$lightGray;

  &__container {
    padding-bottom: 5em;
    @include mq($width: $largescreen) {
      padding-bottom: 84px;
    }
  }

  &__content-container {
    display: flex;
    position: relative;
    flex-direction: column;
    margin: 0 auto;
    @include mq($width: $largescreen) {
      @include container($width: 1110px);
      flex-direction: row;
      justify-content: space-between;
    }
  }

  &__line {
    width: 8px;
    height: 800px;
    position: absolute;
    left: 50%;
    top: 44px;
    transform: translate(-50%, 0);
    background-color: c.$cyan;
    z-index: 0;
    @include mq($width: $largescreen) {
      width: 800px;
      height: 8px;
      left: 0px;
      top: 245px;
      transform: translate(0);
    }
  }

  &__info-container {
    z-index: 1;
  }

  &__info-container:nth-child(3) {
    @include mq($width: $largescreen) {
      margin-top: 44px;
    }
  }

  &__info-container:last-child {
    @include mq($width: $largescreen) {
      margin-top: 88px;
    }
  }

  &__info {
    @include container($width: calc(375px * 0.9));
    background-color: c.$white;
    width: 333.75px;
    height: 300px;
    border-radius: 5px;
    padding: 5em 2.25em 2.5em;
    @include mq($width: $largescreen) {
      padding: 5em 1.5em 2.5em;
    }
  }

  &__logo-container {
    display: grid;
    justify-content: center;
    align-items: center;
    transform: translateY(44px);
    margin: 0 auto;
    height: 88px;
    width: 88px;
    border-radius: 50%;
    background-color: c.$darkViolet;
    @include mq($width: $largescreen) {
      margin-left: 1.5em;
    }
  }

  &__logo {
    position: relative;
  }

  &__heading,
  &__subheading,
  &__title,
  &__desc {
    text-align: center;
  }

  &__heading,
  &__title {
    color: c.$darkerBlue;
  }

  &__subheading,
  &__desc {
    color: c.$grayishViolet;
  }

  &__title,
  &__desc {
    @include mq($width: $largescreen) {
      text-align: left;
    }
  }

  &__heading {
    margin-bottom: 0.5em;
  }

  &__subheading {
    @include container($width: calc(375px * 0.9));
    @include mq($width: $largescreen) {
      @include container($width: 525px);
      margin-bottom: 10px;
    }
  }

  &__title {
    margin-bottom: 0.5em;
  }
}

